arguments.argument:
if(abs(hspeed)<0.2) {
    hspeed=0;
}

var inwater;
inwater = collision_point(x,y,WaterBox,false,false);

if(place_free(x,y+1)) {
    if(inwater) {
        vspeed += 0.36;
        if(vspeed>3) {
            vspeed=3;
        }
    } else {
        vspeed += 0.6;
        if(vspeed>10) {
            vspeed=10;
        }
    }
} else {
    hspeed/=1.1;
}
