arguments.argument:
{
    var xoffset, yoffset;

    xoffset = view_xview[view_current]+x;
    yoffset = view_yview[view_current]+y;

    var button;
    button = -1;

    if (mouse_check_button(mb_left)) {
        if (horizontal) {
            if (mouse_x >= xoffset and mouse_x < xoffset+iconsize*striplength
                and mouse_y >= yoffset and mouse_y < yoffset+iconsize) {
                button = floor((mouse_x-xoffset)/iconsize);
            }
        }else{
            if (mouse_x >= xoffset and mouse_x < xoffset+iconsize
                and mouse_y >= yoffset and mouse_y < yoffset+iconsize*striplength) {
                button = floor((mouse_y-yoffset)/iconsize);
            }
        }
        if (button != -1 and button != clickedbutton)
            event_user(button);
    }
    clickedbutton = button; 
}
