arguments.argument:
var xoffset, yoffset;
xoffset = view_xview[view_current]+x;
yoffset = view_yview[view_current]+y;

var i;
for (i = 0; i < striplength; i+=1) {
    var blend;

    if (i == clickedbutton)
        blend = c_orange;
    else if (i == selected)
        blend = c_green;
    else
        blend = c_white;

    if (horizontal)
        draw_sprite_ext(sprite_index, i, xoffset+i*iconsize, yoffset, 1, 1, 0, blend, 1);
    else
        draw_sprite_ext(sprite_index, i, xoffset, yoffset+i*iconsize, 1, 1, 0, blend, 1);
}
