arguments.argument:
{
    cursor_sprite = CrosshairS;
    global.levelchoice = 1

    menu_create(40, 300, 200, 200, 30);
    menu_setdimmed();

    menu_addlink("Options", '
        instance_destroy();
        instance_create(0,0,OptionsController);
    ');
    menu_addback("Return to Game", '
        instance_destroy();
    ');
    menu_addedit_select("Console", "global.consoleOpen", '
        switch (global.consoleOpen)
        {
            case 0:
                with Console
                {
                    instance_destroy();
                }
                break;

            case 1:
                instance_create(x, y, Console);
                break;
        }
    ');
    menu_add_option(false, "Open Console");
    menu_add_option(true, "Close Console");
    menu_addlink("Disconnect", '
        // Force dedicated mode to off so you can go to main menu instead of just restarting server
        global.dedicatedMode = 0;
        with(Client)
            instance_destroy();

        with(GameServer)
            instance_destroy();
    ');
    menu_addlink("Quit Game", '
        game_end();
    ');

    if global.consoleOpen
    {
        instance_create(x, y, Console)
    }
}
