arguments.argument:
var xoffset, yoffset, xsize, ysize, xshift, yshift;
xoffset = view_xview[0];
yoffset = view_yview[0];
xsize = view_wview[0];
ysize = view_hview[0];
xshift = -320 * global.timerPos;
yshift = 5 * global.timerPos;

var width, height, xpos, ypos;
width = sprite_get_width(sprite_index) * 3;
height = sprite_get_height(sprite_index) * 3;
xpos = xoffset + xsize / 2 - width / 2;
ypos = yoffset + ysize;

draw_set_alpha(1);

draw_sprite_ext(sprite_index, 0, xpos, ypos, 3, 3, 0, c_white, 1);

draw_set_color(c_black);
draw_set_valign(fa_middle);
draw_set_halign(fa_center);

// Red kills
draw_text_transformed(xpos + width / 2 - width / 3.5, ypos - height / 2 + 6, string(global.redCaps), 2, 2, 0);

// Blue kills
draw_text_transformed(xpos + width / 2 + width / 3.5, ypos - height / 2 + 6, string(global.blueCaps), 2, 2, 0);

// Kill limit
draw_text_transformed(xpos + width / 2, ypos - 14, string(killLimit), 2, 2, 0);

//timer
if instance_exists(WinBanner) exit;
draw_set_color(c_white);
draw_set_halign(fa_center);
draw_set_valign(fa_center);

if (global.myself != -1)
{
    if (global.myself.team == TEAM_RED)
        teamoffset = 0;
    else if (global.myself.team == TEAM_BLUE)
        teamoffset = 1;
}

draw_set_halign(fa_right);
draw_sprite_ext(TimerHudS,teamoffset,xoffset+xsize/2 +xshift,yoffset+30+ yshift,3,3,0,c_white,1);
draw_sprite_ext(TimerS,floor(timer/timeLimit*12),xoffset+xsize/2+39 +xshift,yoffset+30+ yshift,3,3,0,c_white,1);
var minutes, secondcounter, seconds;
minutes=floor(timer/1800);
secondcounter=timer-minutes*1800;
seconds=floor(secondcounter/30);    
draw_text_transformed((xoffset+xsize/2-1)-10+xshift,yoffset+32+ yshift,string(minutes),1.5,1.5,0);
draw_text_transformed((xoffset+xsize/2)+xshift,yoffset+32+ yshift,":",1.5,1.5,0);
if (seconds >= 10)
    seconds = string(seconds);
else
    seconds = "0" + string(seconds);
draw_text_transformed((xoffset+xsize/2+20)+xshift,yoffset+32+ yshift,seconds,1.5,1.5,0);
