for (a = 0; a < 360; a += 10) {
    i = instance_create(x, y, Flame);
    i.direction = i;
    i.flameLife = 30;
    i.owner = owner;
    i.ownerPlayer = ownerPlayer;
    i.team = team;
    i.weapon=WEAPON_FLAMETHROWER;
